// screens/HomeScreen.js
import React, { useEffect } from 'react';
import { View, ScrollView, StyleSheet } from 'react-native';
import Carousel from '../components/Carousel';
import PostCard from '../components/PostCard';
import { useFocusEffect } from '@react-navigation/native';
import { useSharedValue } from 'react-native-reanimated';

const HomeScreen = ({ activeTabIndex }) => {
    const items = [
        require('../assets/carousel_img/1.png'),
        require('../assets/carousel_img/8.jpg'),
        require('../assets/carousel_img/3.png'),
    ];

    const posts = [
        {
            id: '1',
            userName: 'Aarav Sharma',
            userImage: 'data:image/jpeg;base64,',
            userAbout: 'Software Engineer at Tech Corp',
            text: 'Excited to announce my new project!',
            image: 'data:image/jpeg;base64,',
            date: '2024-10-03 10:00 AM',
        },
        {
            id: '2',
            userName: 'Vivaan Singh',
            userImage: 'https://via.placeholder.com/100',
            userAbout: 'Product Manager at Innovate LLC',
            text: 'Check out our latest product launch!',
            image: 'https://images.unsplash.com/photo-1576158113928-4c240eaaf360?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8aW1hZ2V8ZW58MHx8MHx8fDA%3D',
            date: '2024-10-02 02:15 PM',
        },
        {
            id: '3',
            userName: 'Ayaan Gupta',
            userImage: 'https://via.placeholder.com/100',
            userAbout: 'Data Scientist at Analytics Co.',
            text: 'Had a great time at the conference yesterday!',
            image: 'https://via.placeholder.com/600x300',
            date: '2024-10-01 08:30 AM',
        },
    ];

    const sortedPosts = posts.sort((a, b) => new Date(b.date) - new Date(a.date));

    // Use focus effect to reset activeTabIndex to 0 when this screen is focused
    useFocusEffect(
        React.useCallback(() => {
            activeTabIndex.value = 0; // Ensure the active tab index is set to Home when focused
        }, [activeTabIndex])
    );

    return (
        <ScrollView
            style={styles.screenContainer}
            contentContainerStyle={styles.contentContainer}
            showsVerticalScrollIndicator={false}
        >
            {/* Carousel */}
            <Carousel items={items} />
            
            {/* Post Cards */}
            {sortedPosts.map(post => (
                <PostCard key={post.id} post={post} />
            ))}
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    screenContainer: {
        flex: 1,
        backgroundColor: '#f9f9f9',
    },
    contentContainer: {
        paddingBottom: 20, // Some padding to avoid cut-off content
    },
});

export default HomeScreen;
